// Program creates 1,000,000 random integers and uses radix sort to sort them 

import java.util.ArrayList;
import java.util.List;

public class Assignment2 {
	public static void main(String[] args) {
		// Creating an ArrayList with 1,000,000 random integers

		final int INT_AMOUNT = 1000000;
		ArrayList<Integer> numbers = new ArrayList<Integer>(INT_AMOUNT);
		for (int i = 0; i < INT_AMOUNT; i++)
			numbers.add((int) (Math.random() * INT_AMOUNT + 1));
		
//		for (Integer e: numbers)
//			System.out.print(e + " ");
//		System.out.println();

		// Sorting the integers using radix sort
		List<Integer>[] bucket = new ArrayList[10];
		for (int i = 0; i < 10; i++)
			bucket[i] = new ArrayList<Integer>();

		for (int i = 1; i <= Math.floor(Math.log10(INT_AMOUNT)) + 1; i++) {
			for (int j = 0; j < INT_AMOUNT; j++) {
				int current = numbers.get(j);
				int key = (int) (current % Math.pow(10, i) / Math.pow(10, i - 1));
				bucket[key].add(current);
			}

			int k = 0;
			for (int r = 0; r < 10; r++) {
				if (!bucket[r].isEmpty()) {
					int bucketSize = bucket[r].size();
					for (int s = 0; s < bucketSize; s++) {
						numbers.set(k++, bucket[r].get(s));
					}
				}
			}
			
			for (int e = 0; e < 10; e++)
				bucket[e].clear();

		}
//		int numberSize = numbers.size();
//		for (Integer e: numbers)
//			System.out.print(e + " ");
	}
}

